package com.class3;

public class IfElseIfTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int month=6;
		if(month==1) {
			System.out.println("This month is January start of the year");
		}else if (month==2) {
			System.out.println("This month is February");
		}else if (month==3) {
			System.out.println("This month is March");
		}else if (month==4) {
			System.out.println("This month is April");
		}else {
			System.out.println("this is another month");
		}
		
		int num1=-1;
		if (num1<0) {
			System.out.println("number is negative");
		}else if (num1==0) {
			System.out.println("number is equal to 0");
		}else if (num1>0) {
			System.out.println("number is positive");
		}else {
			System.out.println("number is unknown");
		}
		
		//Next: Write a Java Program to check whether number is Even or Odd
		
		
	}

}
